{
  description = "Dev shell for system-scripts (Haskell, Stack, HLS, tooling)";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixpkgs-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = import nixpkgs { inherit system; };
        haskellPackages = pkgs.haskell.packages.ghc910;  # keep this if your Stack resolver also uses 9.10.x
      in {
        devShells.default = pkgs.mkShell {
          packages = with pkgs; [
            # Haskell toolchain
            haskellPackages.ghc
            haskellPackages.haskell-language-server
            stack
            cabal-install
            hpack

            # Hoogle from the same HC set (FIXED)
            haskellPackages.hoogle

            # Utils / common native deps
            jq
            curl
            pkg-config
            zlib
            openssl
            libffi
          ];
          shellHook = ''
            export STACK_ROOT="$PWD/.stack"   # or just leave it unset to use ~/.stack
            echo "GHC: $(${haskellPackages.ghc}/bin/ghc --version)"
            echo "HLS: $(${haskellPackages.haskell-language-server}/bin/haskell-language-server --version)"
            echo "hpack: $(hpack --version)"
          '';
        };
      }
    );
}
