    61	
    62	const parseRepoFullName = (fullName: string) => {
    63	  const [owner, repo] = fullName.split("/", 2);
    64	
    65	  if (!owner || !repo) {
    66	    return Effect.fail(
    67	      new BackfillInvalidRepoError({ repoFullName: fullName }),
    68	    );
    69	  }
    70	
    71	  return Effect.succeed({ owner, repo });
    72	};
    73	
    74	const toPayload = (
    75	  run: WorkflowRunSummary,
    76	  repoFullName: string,
    77	): WorkflowRunPayload => {
    78	  const repository = run.repository;
    79	
    80	  return {
    81	    workflow_run: {
    82	      id: run.id,
    83	      run_number: run.run_number,
    84	      run_attempt: run.run_attempt ?? undefined,
    85	      repository_id: repository?.id ?? run.repository_id ?? run.id,
    86	      workflow_id: run.workflow_id ?? undefined,
    87	      workflow_name: run.workflow_name,
    88	      display_title: run.display_title ?? undefined,
    89	      event: run.event,
    90	      status: run.status,
    91	      conclusion: run.conclusion ?? undefined,
    92	      head_branch: run.head_branch ?? undefined,
    93	      head_sha: run.head_sha ?? undefined,
    94	      html_url: run.html_url ?? undefined,
    95	      actor: run.actor?.login ? { login: run.actor.login } : undefined,
    96	      created_at: run.created_at,
    97	      updated_at: run.updated_at,
    98	      run_started_at: run.run_started_at ?? undefined,
    99	      run_completed_at: run.run_completed_at ?? undefined,
   100	    },
   101	    repository: {
   102	      id: repository?.id ?? run.repository_id ?? run.id,
   103	      full_name: repository?.full_name ?? repoFullName,
   104	      visibility: repository?.visibility ?? undefined,
   105	      private: repository?.private ?? undefined,
   106	    },
   107	  } as WorkflowRunPayload;
   108	};
   109	
   110	const fetchRunsPage = (
   111	  owner: string,
   112	  repo: string,
   113	  page: number,
   114	  perPage: number,
   115	) =>
   116	  Effect.flatMap(GithubClient, (client) =>
   117	    client.getJson<RunsResponse>(
   118	      `https://api.github.com/repos/${owner}/${repo}/actions/runs?per_page=${perPage}&page=${page}`,
   119	    ),
   120	  );
   121	
   122	const makeLive = Effect.gen(function* () {
   123	  const rateLimiter = yield* RateLimiter;
   124	
   125	  const runForRepo = (repoFullName: string, options?: RunForRepoOptions) =>
   126	    Effect.gen(function* () {
   127	      const limit = options?.limit ?? DEFAULT_LIMIT;
   128	
   129	      if (!Number.isInteger(limit) || limit <= 0) {
   130	        return yield* Effect.fail(new BackfillLimitError({ limit }));
   131	      }
   132	
   133	      const { owner, repo } = yield* parseRepoFullName(repoFullName);
   134	
   135	      let page = 1;
   136	      let processed = 0;
   137	      const perPage = Math.min(MAX_RUNS_PER_PAGE, limit);
   138	      const enqueueHydration = options?.enqueueHydration ?? false;
   139	
   140	      while (processed < limit) {
   141	        const response = yield* rateLimiter.withRepo(
   142	          repoFullName,
   143	          fetchRunsPage(owner, repo, page, perPage),
   144	        );
   145	        if (response.workflow_runs.length === 0) {
   146	          break;
   147	        }
   148	
   149	        for (const run of response.workflow_runs) {
   150	          if (processed >= limit) {
   151	            break;
   152	          }
   153	
   154	          const payload = toPayload(run, repoFullName);
   155	          yield* persistWorkflowRun(payload, { enqueueHydration });
   156	          processed += 1;
   157	        }
   158	
   159	        if (response.workflow_runs.length < perPage) {
   160	          break;
   161	        }
   162	
   163	        page += 1;
   164	      }
   165	
   166	      yield* Effect.logInfo("backfill completed", {
   167	        repoFullName,
   168	        runsProcessed: processed,
   169	      });
   170	
   171	      return processed;
   172	    });
   173	
   174	  return { runForRepo } as const;
   175	});
