const createWebhook = (input: RegisterWebhookInput) =>
  Effect.flatMap(GithubClient, (client) =>
    client.requestJson<{ id: number; type: string }>({
      url: `https://api.github.com/repos/${input.owner}/${input.repo}/hooks`,
      method: 'POST',
