{"result":"import { Schema } from 'effect';

const NonEmptyString = Schema.String.pipe(Schema.minLength(1));
const PositiveInt = Schema.NumberFromString.pipe(
  Schema.int(),
  Schema.greaterThanOrEqualTo(1),
  Schema.lessThanOrEqualTo(200)
);

export const RunStatusSchema = Schema.Literal(
  'queued',
  'in_progress',
  'completed'
);
