{"result":"import { Context, Effect, Layer, Schema } from 'effect';
import { SqlClient } from '@effect/sql';
import {
  mapRepositoryError,
  RepositoryError,
  toRepositoryError,
} from '../repositoryError.js';

const RunStatusSchema = Schema.Literal('queued', 'in_progress', 'completed');

const RunConclusionSchema = Schema.Literal(
  'action_required',
  'cancelled',
  'failure',
  'neutral',
  'success',
  'skipped',
  'stale',
  'timed_out',
  'startup_failure'
);

export type RunStatus = Schema.Type<typeof RunStatusSchema>;
export type RunConclusion = Schema.Type<typeof RunConclusionSchema>;

const RunSchema = Schema.Struct({
  id: Schema.Number,
  repoId: Schema.Number,
  repoFullName: Schema.NonEmptyString,
  githubRunId: Schema.Number,
  githubRunNumber: Schema.optional(Schema.Number),
  githubRunAttempt: Schema.optional(Schema.Number),
  workflowName: Schema.NonEmptyString,
  workflowId: Schema.optional(Schema.Number),
  displayTitle: Schema.optional(Schema.String),
  event: Schema.NonEmptyString,
  status: RunStatusSchema,
  conclusion: Schema.optional(RunConclusionSchema),
  headBranch: Schema.optional(Schema.String),
  headSha: Schema.optional(Schema.String),
  htmlUrl: Schema.optional(Schema.String),
  actorLogin: Schema.optional(Schema.String),
  createdAt: Schema.Date,
  updatedAt: Schema.Date,
  runStartedAt: Schema.optional(Schema.Date),
  runCompletedAt: Schema.optional(Schema.Date),
  ingestedAt: Schema.Date,
  ingestedUpdatedAt: Schema.Date,
});

export type Run = Schema.Type<typeof RunSchema>;

const UpsertInputSchema = Schema.Struct({
  repoId: Schema.Number,
  repoFullName: Schema.NonEmptyString,
  githubRunId: Schema.Number,
  githubRunNumber: Schema.optional(Schema.Number),
  githubRunAttempt: Schema.optional(Schema.Number),
  workflowName: Schema.NonEmptyString,
  workflowId: Schema.optional(Schema.Number),
  displayTitle: Schema.optional(Schema.String),
  event: Schema.NonEmptyString,
  status: RunStatusSchema,
  conclusion: Schema.optional(RunConclusionSchema),
  headBranch: Schema.optional(Schema.String),
  headSha: Schema.optional(Schema.String),
  htmlUrl: Schema.optional(Schema.String),
  actorLogin: Schema.optional(Schema.String),
  createdAt: Schema.Date,
  updatedAt: Schema.Date,
  runStartedAt: Schema.optional(Schema.Date),
  runCompletedAt: Schema.optional(Schema.Date),
});

export type UpsertInput = Schema.Type<typeof UpsertInputSchema>;

const DEFAULT_QUERY_LIMIT = 50;
const MAX_QUERY_LIMIT = 200;

const CursorSchema = Schema.Struct({
  createdAt: Schema.Date,
  githubRunId: Schema.Number,
});

const QueryInputSchema = Schema.Struct({
  repoFullName: Schema.optional(Schema.NonEmptyString),
  status: Schema.optional(RunStatusSchema),
  from: Schema.optional(Schema.Date),
  to: Schema.optional(Schema.Date),
  search: Schema.optional(Schema.String),
  limit: Schema.optional(Schema.Number),
  cursor: Schema.optional(CursorSchema),
});

export type QueryInput = Schema.Type<typeof QueryInputSchema>;

interface RunRow {
  readonly id: number | string;
  readonly repoId: number | string;
  readonly repoFullName: string;
  readonly githubRunId: number | string;
  readonly githubRunNumber: number | string | null;
  readonly githubRunAttempt: number | string | null;
  readonly workflowName: string;
  readonly workflowId: number | string | null;
  readonly displayTitle: string | null;
  readonly event: string;
  readonly status: RunStatus;
  readonly conclusion: RunConclusion | null;
  readonly headBranch: string | null;
  readonly headSha: string | null;
  readonly htmlUrl: string | null;
  readonly actorLogin: string | null;
  readonly createdAt: string | Date;
  readonly updatedAt: string | Date;
  readonly runStartedAt: string | Date | null;
  readonly runCompletedAt: string | Date | null;
  readonly ingestedAt: string | Date;
  readonly ingestedUpdatedAt: string | Date;
}

const decodeRun = Schema.decodeUnknown(RunSchema);
const decodeUpsertInput = Schema.decodeUnknown(UpsertInputSchema);
const decodeQueryInput = Schema.decodeUnknown(QueryInputSchema);

const toNumber = (value: number | string) =>
  typeof value === 'string' ? Number.parseInt(value, 10) : value;

const toOptionalNumber = (value: number | string | null | undefined) =>
  value == null ? undefined : toNumber(value);

const toDate = (value: string | Date) =>
  value instanceof Date ? value : new Date(value);

const toOptionalDate = (value: string | Date | null | undefined) =>
  value == null ? undefined : toDate(value);

const toOptionalString = (value: string | null | undefined) =>
  value == null ? undefined : value;

const toRun = (row: RunRow) =>
  decodeRun({
    id: toNumber(row.id),
    repoId: toNumber(row.repoId),
    repoFullName: row.repoFullName,
    githubRunId: toNumber(row.githubRunId),
    githubRunNumber: toOptionalNumber(row.githubRunNumber),
    githubRunAttempt: toOptionalNumber(row.githubRunAttempt),
    workflowName: row.workflowName,
    workflowId: toOptionalNumber(row.workflowId),
    displayTitle: toOptionalString(row.displayTitle),
    event: row.event,
    status: row.status,
    conclusion: row.conclusion ?? undefined,
    headBranch: toOptionalString(row.headBranch),
    headSha: toOptionalString(row.headSha),
    htmlUrl: toOptionalString(row.htmlUrl),
    actorLogin: toOptionalString(row.actorLogin),
    createdAt: toDate(row.createdAt),
    updatedAt: toDate(row.updatedAt),
    runStartedAt: toOptionalDate(row.runStartedAt),
    runCompletedAt: toOptionalDate(row.runCompletedAt),
    ingestedAt: toDate(row.ingestedAt),
    ingestedUpdatedAt: toDate(row.ingestedUpdatedAt),
  }).pipe(Effect.mapError(toRepositoryError));

const selectProjection = `
  id,
  repo_id AS "repoId",
  repo_full_name AS "repoFullName",
  github_run_id AS "githubRunId",
  github_run_number AS "githubRunNumber",
  github_run_attempt AS "githubRunAttempt",
  workflow_name AS "workflowName",
  workflow_id AS "workflowId",
  display_title AS "displayTitle",
  event,
  status,
  conclusion,
  head_branch AS "headBranch",
  head_sha AS "headSha",
  html_url AS "htmlUrl",
  actor_login AS "actorLogin",
  created_at AS "createdAt",
  updated_at AS "updatedAt",
  run_started_at AS "runStartedAt",
  run_completed_at AS "runCompletedAt",
  ingested_at AS "ingestedAt",
  ingested_updated_at AS "ingestedUpdatedAt"
`;

const escapeLikePattern = (value: string) =>
  value.replace(/[%_\\]/g, (char) => `\\${char}`);

const tokenizeSearch = (value: string | undefined) =>
  value
    ? value
        .split(/\s+/)
        .map((term) => term.trim())
        .filter((term) => term.length > 0)
    : [];

const normalizeQuery = (input: QueryInput | undefined) =>
  decodeQueryInput(input ?? {}).pipe(
    Effect.map((parsed) => ({
      ...parsed,
      limit: Math.min(
        Math.max(parsed.limit ?? DEFAULT_QUERY_LIMIT, 1),
        MAX_QUERY_LIMIT
      ),
    })),
    Effect.mapError(toRepositoryError)
  );

const makeLive = Effect.gen(function* () {
  const sql = yield* SqlClient.SqlClient;

  const upsert = (input: UpsertInput) =>
    decodeUpsertInput(input).pipe(
      Effect.mapError(toRepositoryError),
      Effect.flatMap((validated) =>
        mapRepositoryError(
          sql<RunRow>`
          INSERT INTO runs (
            repo_id,
            repo_full_name,
            github_run_id,
            github_run_number,
            github_run_attempt,
            workflow_name,
            workflow_id,
            display_title,
            event,
            status,
            conclusion,
            head_branch,
            head_sha,
            html_url,
            actor_login,
            created_at,
            updated_at,
            run_started_at,
            run_completed_at
          )
          VALUES (
            ${validated.repoId},
            ${validated.repoFullName},
            ${validated.githubRunId},
            ${validated.githubRunNumber ?? null},
            ${validated.githubRunAttempt ?? null},
            ${validated.workflowName},
            ${validated.workflowId ?? null},
            ${validated.displayTitle ?? null},
            ${validated.event},
            ${validated.status},
            ${validated.conclusion ?? null},
            ${validated.headBranch ?? null},
            ${validated.headSha ?? null},
            ${validated.htmlUrl ?? null},
            ${validated.actorLogin ?? null},
            ${validated.createdAt},
            ${validated.updatedAt},
            ${validated.runStartedAt ?? null},
            ${validated.runCompletedAt ?? null}
          )
          ON CONFLICT (github_run_id) DO UPDATE SET
            repo_id = EXCLUDED.repo_id,
            repo_full_name = EXCLUDED.repo_full_name,
            github_run_number = EXCLUDED.github_run_number,
            github_run_attempt = EXCLUDED.github_run_attempt,
            workflow_name = EXCLUDED.workflow_name,
            workflow_id = EXCLUDED.workflow_id,
            display_title = EXCLUDED.display_title,
            event = EXCLUDED.event,
            status = EXCLUDED.status,
            conclusion = EXCLUDED.conclusion,
            head_branch = EXCLUDED.head_branch,
            head_sha = EXCLUDED.head_sha,
            html_url = EXCLUDED.html_url,
            actor_login = EXCLUDED.actor_login,
            created_at = EXCLUDED.created_at,
            updated_at = EXCLUDED.updated_at,
            run_started_at = EXCLUDED.run_started_at,
            run_completed_at = EXCLUDED.run_completed_at
          RETURNING ${sql.unsafe(selectProjection)}
        `
        ).pipe(
          Effect.flatMap((rows) =>
            rows.length === 0
              ? Effect.fail(
                  toRepositoryError(new Error('Failed to upsert run'))
                )
              : toRun(rows[0])
          )
        )
      )
    );

  const query = (input?: QueryInput) =>
    normalizeQuery(input).pipe(
      Effect.flatMap((filters) => {
        const whereClauses: Array<string | ReturnType<typeof sql.literal>> = [];

        if (filters.repoFullName) {
          whereClauses.push(sql`repo_full_name = ${filters.repoFullName}`);
        }

        if (filters.status) {
          whereClauses.push(sql`status = ${filters.status}`);
        }

        if (filters.from) {
          whereClauses.push(sql`created_at >= ${filters.from}`);
        }

        if (filters.to) {
          whereClauses.push(sql`created_at <= ${filters.to}`);
        }

        const searchTerms = tokenizeSearch(filters.search);

        for (const term of searchTerms) {
          const pattern = `%${escapeLikePattern(term)}%`;
          whereClauses.push(
            sql`(
              workflow_name ILIKE ${pattern} ESCAPE '\\' OR
              head_branch ILIKE ${pattern} ESCAPE '\\' OR
              head_sha ILIKE ${pattern} ESCAPE '\\' OR
              display_title ILIKE ${pattern} ESCAPE '\\'
            )`
          );
        }

        if (filters.cursor) {
          whereClauses.push(
            sql`(created_at, github_run_id) < (${filters.cursor.createdAt}, ${filters.cursor.githubRunId})`
          );
        }

        const statement =
          whereClauses.length > 0
            ? sql<RunRow>`
              SELECT ${sql.unsafe(selectProjection)}
              FROM runs
              WHERE ${sql.and(whereClauses)}
              ORDER BY created_at DESC, github_run_id DESC
              LIMIT ${filters.limit}
            `
            : sql<RunRow>`
              SELECT ${sql.unsafe(selectProjection)}
              FROM runs
              ORDER BY created_at DESC, github_run_id DESC
              LIMIT ${filters.limit}
            `;

        return mapRepositoryError(statement).pipe(
          Effect.flatMap((rows) =>
            Effect.forEach(rows, toRun, { concurrency: 1 })
          ),
          Effect.map((rows) => rows as ReadonlyArray<Run>)
        );
      })
    );

  const findByGithubRunId = (githubRunId: number) =>
    mapRepositoryError(
      sql<RunRow>`
        SELECT ${sql.unsafe(selectProjection)}
        FROM runs
        WHERE github_run_id = ${githubRunId}
        LIMIT 1
      `.pipe(
        Effect.flatMap((rows) =>
          rows.length === 0
            ? Effect.succeed<Run | undefined>(undefined)
            : toRun(rows[0])
        )
      )
    );

  const findById = (id: number) =>
    mapRepositoryError(
      sql<RunRow>`
        SELECT ${sql.unsafe(selectProjection)}
        FROM runs
        WHERE id = ${id}
        LIMIT 1
      `.pipe(
        Effect.flatMap((rows) =>
          rows.length === 0
            ? Effect.succeed<Run | undefined>(undefined)
            : toRun(rows[0])
        )
      )
    );

  return {
    upsert,
    query,
    findByGithubRunId,
    findById,
  } as const;
});

export class RunsStore extends Context.Tag('ci-dashboard/RunsStore')<
  RunsStore,
  {
    readonly upsert: (
      input: UpsertInput
    ) => Effect.Effect<Run, RepositoryError>;
    readonly query: (
      input?: QueryInput
    ) => Effect.Effect<ReadonlyArray<Run>, RepositoryError>;
    readonly findByGithubRunId: (
      githubRunId: number
    ) => Effect.Effect<Run | undefined, RepositoryError>;
    readonly findById: (
      id: number
    ) => Effect.Effect<Run | undefined, RepositoryError>;
  }
>() {}

export const Live = Layer.effect(RunsStore, makeLive);

export const upsert = (input: UpsertInput) =>
  Effect.flatMap(RunsStore, (store) => store.upsert(input));

export const query = (input?: QueryInput) =>
  Effect.flatMap(RunsStore, (store) => store.query(input));

export const findByGithubRunId = (githubRunId: number) =>
  Effect.flatMap(RunsStore, (store) => store.findByGithubRunId(githubRunId));

export const findById = (id: number) =>
  Effect.flatMap(RunsStore, (store) => store.findById(id));

export const _test = {
  tokenizeSearch,
  escapeLikePattern,
} as const;"}

