        WHERE full_name = ${fullName}
        LIMIT 1
      `.pipe(
        Effect.flatMap((rows) =>
          rows.length === 0
            ? Effect.succeed<Repo | undefined>(undefined)
            : toRepo(rows[0])
        )
      )
    );

  const markBackfilledAt = (repoId: number, at: Date = new Date()) =>
    mapRepositoryError(
      sql<RepoRow>`
        UPDATE repos
        SET backfilled_at = ${at}, updated_at = NOW()
        WHERE id = ${repoId}
        RETURNING ${sql.unsafe(selectProjection)}
