{"result":"import * as TSemaphore from 'effect/TSemaphore';

import { GithubClientResponseError } from './client.js';

const MAX_ATTEMPTS = 5;
const BASE_DELAY_MS = 1_000;
const MAX_DELAY_MS = 60_000;

const isRateLimitError = (error: unknown): error is GithubClientResponseError =>
  error instanceof GithubClientResponseError &&
  (error.status === 403 || error.status === 429);

const makeDelay = (attempt: number, jitterMs: number) =>
  Math.min(BASE_DELAY_MS * 2 ** attempt, MAX_DELAY_MS) + jitterMs;

export class RateLimiter extends Context.Tag('ci-dashboard/RateLimiter')<
  RateLimiter,
  {
    readonly withRepo: <R, E, A>(
      repoFullName: string,
      effect: Effect.Effect<A, E, R>
    ) => Effect.Effect<A, E, R>;
  }
>() {}

const makeLive = Effect.gen(function* () {
  const semaphoreMap = yield* Ref.make(
    new Map<string, TSemaphore.TSemaphore>()
  );

  const getSemaphore = (repoFullName: string) =>
    Effect.gen(function* () {
      const current = yield* Ref.get(semaphoreMap);
      const existing = current.get(repoFullName);
      if (existing) {
        return existing;
      }

      const semaphore = yield* TSemaphore.make(1);
      yield* Ref.update(semaphoreMap, (map) => {
        const next = new Map(map);
        next.set(repoFullName, semaphore);
        return next;
      });
      return semaphore;
    });

  const withRepo = <R, E, A>(
    repoFullName: string,
    effect: Effect.Effect<A, E, R>
  ) =>
    Effect.gen(function* () {
      const semaphore = yield* getSemaphore(repoFullName);

      const run = (attempt: number): Effect.Effect<A, E, R> =>
        effect.pipe(
          Effect.catchIf(isRateLimitError, (error) =>
            Effect.gen(function* () {
              const nextAttempt = attempt + 1;
              if (nextAttempt > MAX_ATTEMPTS) {
                return yield* Effect.fail(error as E);
              }

              const jitter = yield* Random.nextIntBetween(0, 1_000);
              const delay = makeDelay(attempt, jitter);

              yield* Effect.logWarning(
                'github secondary rate limit encountered',
                {
                  repoFullName,
                  status: error.status,
                  attempt: nextAttempt,
                  delayMs: delay,
                }
              );

              yield* Effect.sleep(Duration.millis(delay));
              return yield* run(nextAttempt);
            })
          )
        );

      return yield* TSemaphore.withPermits(semaphore, 1)(run(0));
    });

  return {
    withRepo,
  } as const;
});

export const Live = Layer.effect(RateLimiter, makeLive);

export const withRepo = <R, E, A>(
  repoFullName: string,
  effect: Effect.Effect<A, E, R>
) =>
  Effect.flatMap(RateLimiter, (service) =>
    service.withRepo(repoFullName, effect)
  );"}

