import {
  Live as RateLimiterLive,
  RateLimitExceededError,
  RequestRateLimiter,
  rateLimitMiddleware,
} from './rateLimit.js';

const provideRequest = (url: string, headers: Record<string, string> = {}) => {
  const request = HttpServerRequest.fromWeb(
    new Request(url, {
      method: 'GET',
      headers,
    })
  );

  return Layer.mergeAll(
    Layer.succeed(HttpServerRequest.HttpServerRequest, request),
    Layer.succeed(
      HttpServerRequest.ParsedSearchParams,
      HttpServerRequest.searchParamsFromURL(new URL(url))
    )
  );
};

describe('RequestRateLimiter', () => {
  it.effect('limits repeated keys', () =>
    Effect.provide(
      Effect.gen(function* () {
        const limiter = yield* RequestRateLimiter;
        const bucket = {
          namespace: 'test',
          windowMs: 60_000,
          maxRequests: 2,
        } as const;

        yield* limiter.consume('client-1', bucket);
        yield* limiter.consume('client-1', bucket);

        yield* limiter
          .consume('client-1', bucket)
          .pipe(
            Effect.matchEffect({
              onSuccess: () => Effect.fail(new Error('expected failure')),
              onFailure: (error) =>
                Effect.sync(() => {
                  expect(error).toBeInstanceOf(RateLimitExceededError);
                  expect(error.retryAfterMs).toBeGreaterThan(0);
                }),
            })
          );
      }),
      RateLimiterLive
    )
  );
});

describe('rateLimitMiddleware', () => {
  it.effect('returns 429 after exceeding the limit', () =>
    Effect.gen(function* () {
      const app = rateLimitMiddleware({
        namespace: 'http-test',
        windowMs: 60_000,
        maxRequests: 1,
        deriveKey: () => 'fixed-client',
      })(
        HttpServerResponse.json({ ok: true })
      );

      const layer = Layer.mergeAll(
        RateLimiterLive,
        provideRequest('http://localhost/api/repos')
      );

      const first = yield* Effect.provide(app, layer).pipe(
        Effect.map(HttpServerResponse.toWeb)
      );
      expect(first.status).toBe(200);

      const second = yield* Effect.provide(app, layer).pipe(
        Effect.map(HttpServerResponse.toWeb)
      );
      expect(second.status).toBe(429);
      expect(second.headers.get('retry-after')).toBe('60');
    })
  );
});"}

