            const response = { status: 500, body: `Internal Error: ${error.error}` };
            yield* Effect.logError(`${response.status} ${response.body}`);
            return response;
          })
      })
    );

    yield* Effect.logInfo(`Final Response: ${JSON.stringify(result)}`);
