export class WorkflowRunPayloadError extends Data.TaggedError(
  'WorkflowRunPayloadError'
)<{
  readonly cause: unknown;
}> {}

export class WorkflowRunHandler extends Context.Tag(
  'ci-dashboard/WorkflowRunHandler'
)<
  WorkflowRunHandler,
  {
    readonly handle: (
      payload: unknown
    ) => Effect.Effect<
      RunsStore.Run,
      RepositoryError | WorkflowRunPayloadError,
      | RunsStore.RunsStore
      | EventsHub.EventsHub
      | ReposStore.ReposStore
      | RunHydrationQueue
    >;
  }
>() {}

export const Live = Layer.effect(
  WorkflowRunHandler,
  Effect.gen(function* () {
    const eventsHub = yield* EventsHub.EventsHub;

    return {
      handle: (payload: unknown) =>
        Effect.gen(function* () {
          const decoded = yield* Schema.decodeUnknown(WorkflowRunPayloadSchema)(
            payload
          ).pipe(
            Effect.catchTag(
              'ParseError',
              (cause) => new WorkflowRunPayloadError({ cause })
            )
          );
