{"result":"import { Context, Data, Effect, Layer, Ref } from 'effect';
import * as HttpMiddleware from '@effect/platform/HttpMiddleware';
import * as HttpServerRequest from '@effect/platform/HttpServerRequest';
import * as HttpServerResponse from '@effect/platform/HttpServerResponse';

const defaultWindowMs = 60_000;
const defaultMaxRequests = 120;

export interface RateLimitBucketConfig {
  readonly namespace: string;
  readonly windowMs: number;
  readonly maxRequests: number;
}

export interface RateLimitMiddlewareOptions {
  readonly namespace?: string;
  readonly windowMs?: number;
  readonly maxRequests?: number;
  readonly deriveKey?: (request: HttpServerRequest.HttpServerRequest) => string;
}

export class RateLimitExceededError extends Data.TaggedError(
  'RateLimitExceededError'
)<{
  readonly key: string;
  readonly bucket: string;
  readonly retryAfterMs: number;
}> {}

export class RequestRateLimiter extends Context.Tag(
  'ci-dashboard/RequestRateLimiter'
)<
  RequestRateLimiter,
  {
    readonly consume: (
      key: string,
      config: RateLimitBucketConfig
    ) => Effect.Effect<void, RateLimitExceededError>;
  }
>() {}

interface BucketState {
  readonly count: number;
  readonly resetAt: number;
}

const makeLive = Effect.gen(function* () {
  const buckets = yield* Ref.make(new Map<string, BucketState>());

  const consume = (key: string, config: RateLimitBucketConfig) =>
    Effect.gen(function* () {
      const now = yield* Effect.sync(() => Date.now());
      const bucketKey = `${config.namespace}:${key}`;

      const { allowed, retryAfterMs } = yield* Ref.modify(buckets, (map) => {
        const current = map.get(bucketKey);
        const next = new Map(map);

        if (!current || now >= current.resetAt) {
          next.set(bucketKey, {
            count: 1,
            resetAt: now + config.windowMs,
          });
          return [{ allowed: true, retryAfterMs: config.windowMs }, next];
        }

        if (current.count < config.maxRequests) {
          next.set(bucketKey, {
            count: current.count + 1,
            resetAt: current.resetAt,
          });
          return [{ allowed: true, retryAfterMs: current.resetAt - now }, next];
        }

        return [
          { allowed: false, retryAfterMs: Math.max(0, current.resetAt - now) },
          map,
        ];
      });

      if (allowed) {
        return;
      }

      return yield* Effect.fail(
        new RateLimitExceededError({
          key,
          bucket: config.namespace,
          retryAfterMs,
        })
      );
    });

  return {
    consume,
  } as const;
});

export const Live = Layer.effect(RequestRateLimiter, makeLive);

const defaultKey = (request: HttpServerRequest.HttpServerRequest) =>
  request.headers['x-forwarded-for']
    ?.split(',')
    .map((value) => value.trim())
    .find((value) => value.length > 0) ??
  request.headers['x-real-ip'] ??
  request.headers['cf-connecting-ip'] ??
  'global';

export const rateLimitMiddleware = (options?: RateLimitMiddlewareOptions) =>
  HttpMiddleware.make((app) =>
    Effect.gen(function* () {
      const request = yield* HttpServerRequest.HttpServerRequest;
      const limiter = yield* RequestRateLimiter;

      const namespace = options?.namespace ?? 'http';
      const windowMs = options?.windowMs ?? defaultWindowMs;
      const maxRequests = options?.maxRequests ?? defaultMaxRequests;
      const key = (options?.deriveKey ?? defaultKey)(request);

      return yield* limiter
        .consume(key, {
          namespace,
          windowMs,
          maxRequests,
        })
        .pipe(
          Effect.matchEffect({
            onSuccess: () => app,
            onFailure: (error) =>
              Effect.zipRight(
                Effect.logWarning('http request rate limited', {
                  key: error.key,
                  namespace: error.bucket,
                  retryAfterMs: error.retryAfterMs,
                }),
                HttpServerResponse.json(
                  {
                    error: 'rate_limited',
                    detail: 'Too many requests, please retry later',
                    retryAfterMs: error.retryAfterMs,
                  },
                  {
                    status: 429,
                    headers: {
                      'Retry-After': Math.max(
                        1,
                        Math.ceil(error.retryAfterMs / 1000)
                      ).toString(),
                    },
                  }
                )
              ),
          })
        );
    })
  );

export const withRateLimit = (key: string, config: RateLimitBucketConfig) =>
  Effect.flatMap(RequestRateLimiter, (service) => service.consume(key, config));"}

