const SIGNATURE_PREFIX = 'sha256=';

type Payload = string | Uint8Array | ArrayBuffer;

const toUint8Array = (payload: Payload): Uint8Array => {
  if (typeof payload === 'string') {
    return Buffer.from(payload, 'utf8');
  }
  if (payload instanceof Uint8Array) {
    return payload;
  }
  return new Uint8Array(payload);
};

export class MissingSignatureError extends Data.TaggedError(
  'MissingSignatureError'
)<{
  readonly headerName: string;
}> {}

export class InvalidSignatureFormatError extends Data.TaggedError(
  'InvalidSignatureFormatError'
)<{
  readonly detail: string;
}> {}

export class SignatureMismatchError extends Data.TaggedError(
  'SignatureMismatchError'
)<{
  readonly received: string;
}> {}

export class GithubWebhookVerifier extends Context.Tag(
  'ci-dashboard/GithubWebhookVerifier'
)<
  GithubWebhookVerifier,
  {
    readonly verify: (
      payload: Payload,
      signatureHeader: string | null | undefined
    ) => Effect.Effect<
      void,
      | MissingSignatureError
      | InvalidSignatureFormatError
      | SignatureMismatchError
    >;
  }
>() {}

const makeLive = Effect.gen(function* () {
  const { webhookSecret } = yield* Config;
  const secret = Buffer.from(webhookSecret, 'utf8');

  const verify = (
    payload: Payload,
    signatureHeader: string | null | undefined
  ) =>
    Effect.gen(function* () {
      if (!signatureHeader || signatureHeader.length === 0) {
        return yield* Effect.fail(
          new MissingSignatureError({ headerName: 'X-Hub-Signature-256' })
        );
      }

      if (!signatureHeader.startsWith(SIGNATURE_PREFIX)) {
        return yield* Effect.fail(
          new InvalidSignatureFormatError({
            detail: 'signature header must start with sha256=',
          })
        );
      }

      const signature = signatureHeader.slice(SIGNATURE_PREFIX.length);
      if (signature.length === 0) {
        return yield* Effect.fail(
          new InvalidSignatureFormatError({
            detail: 'signature hash is empty',
          })
        );
