import * as ReposStore from '../db/schema/repos.js';
import { RepositoryError } from '../db/repositoryError.js';
import { RunsBackfill } from './backfill.js';

const logBackfillError = (repoFullName: string, error: unknown) =>
  Effect.logError('backfill failed', {
    repoFullName,
    error: error instanceof Error ? error.message : String(error),
  });

export class BackfillScheduler extends Context.Tag(
  'ci-dashboard/BackfillScheduler'
)<
  BackfillScheduler,
  {
    readonly run: Effect.Effect<void, RepositoryError>;
  }
>() {}

export const Live = Layer.effect(
  BackfillScheduler,
  Effect.gen(function* () {
    const repos = yield* ReposStore.ReposStore;
    const runsBackfill = yield* RunsBackfill;

    return {
      run: Effect.gen(function* () {
        const activeRepos = yield* repos.listActive;

        const pending = activeRepos.filter((repo) => repo.backfilledAt == null);

        if (pending.length === 0) {
          yield* Effect.logDebug('no repositories pending backfill');
          return;
        }

        yield* Effect.logInfo('starting backfill scheduler', {
          repos: pending.map((repo) => repo.fullName),
        });

        yield* Effect.forEach(
          pending,
          (repo) =>
            Effect.gen(function* () {
              const count = yield* runsBackfill.runForRepo(repo.fullName, {
                enqueueHydration: false,
              });
              yield* Effect.logInfo('repo backfill completed', {
                repoFullName: repo.fullName,
                runsProcessed: count,
              });
              yield* repos.markBackfilledAt(repo.id);
            }).pipe(
              Effect.catchAll((error) => logBackfillError(repo.fullName, error))
            ),
          { concurrency: 1 }
        );
      }),
    };
  })
);"}

